  "type": "update",
  "ns": "meraki_prod.cartmodels",
  "command": {
    "q": {},
    "u": {
      "$set": {
        "pushed_to_kafka": true
      }
    },
    "multi": true,
    "upsert": false
  },
  "planSummary": "COLLSCAN",
  "keysExamined": 0,
